{
  "description": "Subscribe to names being spotted by camera",
  "type": "Subscription",
  "entities": [{"type": "Camera"}],
  "watchedAttributes": ["names"],
  "notification": {
    "format": "keyValues",
    "attributes": ["names", "locatedIn"],
    "endpoint": {
      "uri": "https://asia-southeast1-composite-silo-413107.cloudfunctions.net/forward_names",
      "accept": "application/json"
    }
  },
  "@context": "{{context-ld}}"
}
